"use strict";var Enemy=function(e,t){this.x=e,this.y=t,this.sprite="images/enemy-bug.png"};Enemy.prototype.update=function(e){},Enemy.prototype.render=function(){ctx.drawImage(Resources.get(this.sprite),this.x,this.y)};var Player=function(e,t){this.x=e,this.y=t,this.sprite="images/char-cat-girl.png"};Player.prototype.render=function(){ctx.drawImage(Resources.get(this.sprite),this.x,this.y)},Player.prototype.update=function(e,t){this.x=e,this.y=t},Player.prototype.handleInput=function(e){switch(e){case"left":this.update(this.x--,this.y);break;case"right":this.update(this.x++,this.y);break;case"up":this.update(this.x,this.y++);break;case"down":this.update(this.x,this.y--)}};var player=new Player(100,100),enemy1=new Enemy(10,70),enemy2=new Enemy(10,140),enemy3=new Enemy(10,210),enemy4=new Enemy(185,140),allEnemies=[enemy1,enemy2,enemy3,enemy4];document.addEventListener("keyup",function(e){var t={37:"left",38:"up",39:"right",40:"down"};player.handleInput(t[e.keyCode])});